import { expect } from 'chai'
import { shallowMount } from '@vue/test-utils'
import Todo from '@/components/Todo/Todo.vue'
import { getData } from '../../src/assets/mockData';

let wrapper;
let title;


describe('Todo.vue', () => {
    title = 'Todo App';
	wrapper = shallowMount(Todo, {
		propsData: { title },
		data() {
			return { todos: getData.todos };
		},
	});


    it('should textbox is appear', () => {
		expect(wrapper.find('#text-input').exists()).toBeTruthy();
	});
    it('should add button is appear', () => {
		expect(wrapper.find('#add-button').exists()).toBeTruthy();
	});
    it('should list is appear', () => {
		expect(wrapper.find('#list').exists()).toBeTruthy();
	});
})
